   end if
   do axis=0,nd-1
   do side=0,1
     if( boundaryCondition(side,axis).gt.0 )then
       ! write(*,'(" bcOpt: side,axis,bc=",3i2)') side,axis,boundaryCondition(side,axis)
       n1a=gridIndexRange(0,0)
