        j3=i3-is3
        u(j1,j2,j3,tc)=(5.*u(i1,i2,i3,tc)-10.*u(i1+is1,i2+is2,i3+is3,tc)+10.*u(i1+2*is1,i2+2*is2,i3+2*is3,tc)-5.*u(i1+3*is1,i2+3*is2,i3+3*is3,tc)+u(i1+4*is1,i2+4*is2,i3+4*is3,tc)) 

