     end if

    if( tbc(side,axis).ne.dirichlet .and. tbc(side,axis).gt.0 )then
       do i3=n3a,n3b
       do i2=n2a,n2b
       do i1=n1a,n1b
        ! extrap first ghost
        j1=i1-is1
        j2=i2-is2
        j3=i3-is3
        u(j1,j2,j3,tc)=(5.*u(i1,i2,i3,tc)-10.*u(i1+is1,i2+is2,i3+is3,tc)+10.*u(i1+2*is1,i2+2*is2,i3+2*is3,tc)-5.*u(i1+3*is1,i2+3*is2,i3+3*is3,tc)+u(i1+4*is1,i2+4*is2,i3+4*is3,tc))

        if( orderOfAccuracy.gt.2 ) then
