          k2=j2-is2
          k3=j3-is3
          u(k1,k2,k3,tc)=(5.*u(j1,j2,j3,tc)-10.*u(j1+is1,j2+is2,j3+is3,tc)+10.*u(j1+2*is1,j2+2*is2,j3+2*is3,tc)-5.*u(j1+3*is1,j2+3*is2,j3+3*is3,tc)+u(j1+4*is1,j2+4*is2,j3+4*is3,tc))
        end if 
        
       end do
       end do
       end do
