   if(  nd.eq.3 )then
    if( boundaryCondition(0,2).lt.0 )then
      extra3a=max(0,extra3a) ! over-ride extraForNeumann=-1 : assign ends in periodic directions (or internal parallel boundaries)
    else if( boundaryCondition(0,2).eq.0 )then
